en.typescript_lesson1_ex2_slide2_content:
const x: number = 10;
const y: number = 5;
console.log(x + y);

Output: 15

en.typescript_lesson1_ex5_slide2_content:
const name = 'Alice';
console.log(`Hello ${name}!`);

Output: Hello Alice!

en.typescript_lesson1_ex6_slide2_content:
Add [] after the type name.

const items: string[] = ['a', 'b'];
console.log(items[0]);  // a

en.typescript_lesson1_ex7_slide2_content:
const isOpen: boolean = false;
if (!isOpen) {
    console.log('Closed');
}

! means 'not'

en.typescript_lesson1_ex8_slide2_content:
const hour = 15;
if (hour < 12) {
    console.log('Morning');
} else {
    console.log('Afternoon');
}

en.typescript_lesson1_ex10_slide2_content:
const animals = ['dog', 'cat'];
for (const animal of animals) {
    console.log(animal);
}
// dog
// cat

en.typescript_lesson1_ex11_slide2_content:
type Pet = { type: string; name: string };
const myPet: Pet = { type: 'dog', name: 'Pochi' };
console.log(myPet.name);  // Pochi

en.typescript_lesson1_ex12_slide2_content:
function greet(name: string) {
    console.log(`Hello ${name}`);
}
greet('TypeScript');
// Hello TypeScript

en.typescript_lesson1_ex13_slide2_content:
function logger(text: string): void {
    console.log(`LOG: ${text}`);
}
logger('Start');
// LOG: Start

en.typescript_lesson2_ex1_slide2_content:
let value: string | number;
value = 'hello';  // OK!
value = 42;       // OK!

en.typescript_lesson2_ex2_slide2_content:
interface User {
    name: string;
    age: number;
}
// Error! age is missing
const user: User = { name: 'Taro' };

en.typescript_lesson2_ex3_slide2_content:
interface User {
    name: string;
    email?: string;  // optional
}
const user1: User = { name: 'Taro' };  // OK!
const user2: User = { name: 'Hanako', email: 'h@example.com' };  // OK!

en.typescript_lesson2_ex4_slide2_content:
type ID = string | number;

let userId: ID = 'abc';
let orderId: ID = 123;

en.typescript_lesson2_ex5_slide2_content:
interface User {
    readonly id: number;
    name: string;
}
const user = { id: 1, name: 'Taro' };
user.name = 'Jiro';  // OK!
user.id = 2;         // Error!

en.typescript_lesson2_ex7_slide2_content:
function identity<T>(arg: T): T {
    return arg;
}
const str = identity('hello');  // string
const num = identity(42);       // number

en.typescript_lesson2_ex8_slide2_content:
interface Box<T> {
    value: T;
}
const numBox: Box<number> = { value: 42 };
const strBox: Box<string> = { value: 'hi' };

en.typescript_lesson2_ex9_slide2_content:
const user = { name: 'Taro', age: 20 };
type UserType = typeof user;
// { name: string; age: number; }

en.typescript_lesson2_ex10_slide2_content:
interface A { a: string; }
interface B { b: number; }
interface C extends A, B {
    c: boolean;
}
// C has a, b, and c!

en.typescript_lesson3_ex1_slide2_content:
interface User { name: string; age: number; }
type PartialUser = Partial<User>;
// { name?: string; age?: number; }

Useful for partial updates!

en.typescript_lesson3_ex2_slide2_content:
interface Props { name?: string; age?: number; }
type RequiredProps = Required<Props>;
// { name: string; age: number; }

? marks are removed!

en.typescript_lesson3_ex3_slide2_content:
interface User { id: number; name: string; email: string; }
type UserPreview = Pick<User, 'id' | 'name'>;
// { id: number; name: string; }

email is not included!

en.typescript_lesson3_ex4_slide2_content:
interface User { id: number; name: string; password: string; }
type PublicUser = Omit<User, 'password'>;
// { id: number; name: string; }

password is removed!

en.typescript_lesson3_ex5_slide2_content:
type Status = 'pending' | 'done' | 'error';
type StatusColors = Record<Status, string>;

const colors: StatusColors = {
  pending: 'yellow',
  done: 'green',
  error: 'red'
};
// Only these 3 keys allowed!

en.typescript_lesson3_ex6_slide2_content:
function getUser() {
  return { id: 1, name: 'Alice' };
}
type User = ReturnType<typeof getUser>;
// { id: number; name: string; }

en.typescript_lesson3_ex7_slide2_content:
type IsString<T> = T extends string ? 'yes' : 'no';

type A = IsString<string>;  // 'yes'
type B = IsString<number>;  // 'no'

en.typescript_lesson3_ex8_slide2_content:
type GetArrayElement<T> = T extends (infer U)[] ? U : never;

type A = GetArrayElement<string[]>; // string
// 'What's inside string[]? string!'

en.typescript_lesson3_ex9_slide2_content:
interface User { id: number; name: string; }
type UserKeys = keyof User; // 'id' | 'name'

function getValue<K extends keyof User>(user: User, key: K) {
  return user[key];
}
getValue(user, 'name');  // OK!
getValue(user, 'age');   // Error!

en.typescript_lesson3_ex10_slide2_content:
type Nullable<T> = {
  [K in keyof T]: T[K] | null;
};

interface User { name: string; }
type NullableUser = Nullable<User>;
// { name: string | null; }

en.typescript_lesson4_ex1_slide2_content:
function isString(x: unknown): x is string {
  return typeof x === 'string';
}

if (isString(value)) {
  // value is string here!
  console.log(value.toUpperCase());
}

en.typescript_lesson4_ex2_slide2_content:
type Fish = { swim: () => void };
type Bird = { fly: () => void };

function move(animal: Fish | Bird) {
  if ('swim' in animal) {
    animal.swim();  // Fish!
  } else {
    animal.fly();   // Bird!
  }
}

en.typescript_lesson4_ex3_slide2_content:
type Circle = { kind: 'circle'; radius: number };
type Square = { kind: 'square'; size: number };

function area(s: Circle | Square) {
  switch (s.kind) {
    case 'circle': return Math.PI * s.radius ** 2;
    case 'square': return s.size ** 2;
  }
}

en.typescript_lesson4_ex4_slide2_content:
type Color = 'red' | 'blue';

function handle(c: Color) {
  switch (c) {
    case 'red': return '#ff0000';
    case 'blue': return '#0000ff';
    default:
      const _: never = c;  // If case missed, error here!
      return _;
  }
}

en.typescript_lesson4_ex5_slide2_content:
type Color = 'red' | 'blue';
type Size = 'small' | 'large';
type Style = `${Size}-${Color}`;
// 'small-red' | 'small-blue' | 'large-red' | 'large-blue'

4 combinations automatically!

en.typescript_lesson4_ex6_slide2_content:
type T = 'a' | 'b' | 'c';
type Without_A = Exclude<T, 'a'>;
// 'b' | 'c'

'a' is excluded!

en.typescript_lesson4_ex7_slide2_content:
type T = 'a' | 'b' | 'c';
type Only_AB = Extract<T, 'a' | 'b'>;
// 'a' | 'b'

Only 'a' and 'b' kept!

en.typescript_lesson4_ex8_slide2_content:
type T = string | null | undefined;
type NonNull = NonNullable<T>;
// string

null and undefined removed!

en.typescript_lesson4_ex9_slide2_content:
type Fn = (a: string, b: number) => void;
type Params = Parameters<Fn>;
// [string, number]

'First is string, second is number'

en.typescript_lesson4_ex10_slide2_content:
type P = Promise<string>;
type T = Awaited<P>;
// string

'Promise<string> resolves to string!'

