// function でメモ化関数を定義
function memoize(fn) {
  // const でキャッシュオブジェクトを作成
  const cache = {};
  return (x) => {
    // in でオブジェクトにキーが存在するかチェック
    if (!(x in cache)) {
      // キャッシュに結果を保存
      cache[x] = fn(x);
    }
    // return でキャッシュから返す
    return cache[x];
  };
}
// メモ化されたsquare関数を作成
const square = memoize(x => x * x);
// 結果を出力
console.log(square(5));
console.log(square(5));

// const でパイプライン関数を定義
