  fns.reduce((v, f) => f(v), x);

// +1, *2, -3 の順で関数を連結
const process = pipe(
  // +1 する関数
  x => x + 1,
  // *2 する関数
  x => x * 2,
  // -3 する関数
  x => x - 3
);
// 結果を出力
console.log(process(5));

