// WeakMap で弱参照のMapを作成
const privateData = new WeakMap();

class User {
  constructor(name) {
    privateData.set(this, { password: 'secret' });
    this.name = name;
  }
  getPassword() {
    return privateData.get(this).password;
  }
}

const user = new User('Taro');
console.log(user.getPassword());

